import{_ as e,c as r,o as a,d as t}from"./app.28458000.js";const n='{"title":"介绍","description":"","frontmatter":{"title":"介绍","lang":"zh-CN"},"headers":[{"level":2,"title":"简介","slug":"简介"},{"level":2,"title":"需要掌握的知识","slug":"需要掌握的知识"},{"level":2,"title":"模板","slug":"模板"}],"relativePath":"guide/introduce.md","lastUpdated":1636812404319}',i={},o=[t('<h1 id="介绍" tabindex="-1">介绍 <a class="header-anchor" href="#介绍" aria-hidden="true">#</a></h1><p><span style="background-image:linear-gradient(135deg, #4253d8, #d333ba);-webkit-background-clip:text;color:transparent;font-size:18px;">还请容我做一下自我介绍</span></p><h2 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-hidden="true">#</a></h2><p><a href="https://github.com/matrix-zyh/uni-app-matrix-admin" target="_blank" rel="noopener noreferrer">UniApp Matrix Admin</a> 是一个基于 <a href="https://uniapp.dcloud.io/" target="_blank" rel="noopener noreferrer">uni-app</a>、<a href="https://github.com/weilanwl/ColorUI" target="_blank" rel="noopener noreferrer">Color UI</a>、<a href="https://www.uviewui.com/" target="_blank" rel="noopener noreferrer">uView UI</a> 共同组建前端跨平台解决方案，目标是为开发 <code>Android</code>、<code>ios</code>、<code>小程序</code> 等移动端提供开箱即用的解决方案。项目中会使用前端较新的技术栈，可以作为项目的启动模版，以帮助你快速搭建移动端产品原型。当然，您也可以作为一个示例，用于学习技术。该项目会一直维护下去，并将其应用在项目中。</p><h2 id="需要掌握的知识" tabindex="-1">需要掌握的知识 <a class="header-anchor" href="#需要掌握的知识" aria-hidden="true">#</a></h2><p>本项目需要一定前端基础知识，请确保掌握 Vue 的基础知识，以便能处理一些常见的问题。 建议在开发前先学一下以下内容，提前了解和学习这些知识，会对项目理解非常有帮助:</p><ul><li><a href="https://cn.vuejs.org/v2/guide/" target="_blank" rel="noopener noreferrer">Vue2</a></li><li><a href="https://next.vuex.vuejs.org/zh/index.html" target="_blank" rel="noopener noreferrer">Vuex</a></li><li><a href="https://hhyang.cn/v2/" target="_blank" rel="noopener noreferrer">uni-simple-router</a></li><li><a href="https://github.com/weilanwl/ColorUI" target="_blank" rel="noopener noreferrer">Color UI</a></li><li><a href="https://www.uviewui.com/" target="_blank" rel="noopener noreferrer">uView UI</a></li></ul><h2 id="模板" tabindex="-1">模板 <a class="header-anchor" href="#模板" aria-hidden="true">#</a></h2><ul><li><p><a href="https://github.com/matrix-zyh/uni-app-matrix-admin" target="_blank" rel="noopener noreferrer">UniApp Matrix Admin</a> 该版本主要是提供一些 <code>Demo</code> 示例及插件的使用集成方式，主要用于参考。如果对项目不是很熟悉，不建议在此基础上进行开发，请使用下方提供的精简版本。</p></li><li><p><a href="https://github.com/matrix-zyh/uni-app-admin-lite" target="_blank" rel="noopener noreferrer">UniApp Admin Lite</a> <code>UniApp Matrix Admin</code> 精简版本。删除了相关示例、无用文件及功能、依赖。可以根据自身需求安装对应的依赖。</p></li></ul>',9)];var l=e(i,[["render",function(e,t,n,i,l,p){return a(),r("div",null,o)}]]);export default l;export{n as __pageData};
